mapper.select:
    SELECT 
    *,
    photo.id as img,
    ROUND(
        6378.138 * 2 * ASIN(
            SQRT(
                POW(
                    SIN(
                        (
                            #{0} * PI() / 180 - latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS(#{0} * PI() / 180) * COS(latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{1} * PI() / 180 - longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
            )
        ) * 1000
    ) AS distance
FROM
    business,photo
where business.id=photo.business_id
HAVING
	distance <= #{2}
ORDER BY
    distance ASC
LIMIT 1,150

    SELECT 
    *,
    photo.id as img,
    ROUND(
        6378.138 * 2 * ASIN(
            SQRT(
                POW(
                    SIN(
                        (
                            #{0} * PI() / 180 - latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS(#{0} * PI() / 180) * COS(latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{1} * PI() / 180 - longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
            )
        ) * 1000
    ) AS distance
FROM
    business,photo
where business.id=photo.business_id
HAVING
	distance <= #{3} and  distance >= #{2}
ORDER BY
    distance ASC
LIMIT 1,150

    SELECT 
    *,
    photo.id as img,
    ROUND(
        6378.138 * 2 * ASIN(
            SQRT(
                POW(
                    SIN(
                        (
                            #{0} * PI() / 180 - latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS(#{0} * PI() / 180) * COS(latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{1} * PI() / 180 - longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
            )
        ) * 1000
    ) AS distance
FROM
    business,photo
where business.id=photo.business_id
HAVING
	distance >= #{2}
ORDER BY
    distance ASC
LIMIT 1,150
